<script>
    let menuChooses = document.querySelector('.menuChooses');
    let oldPosiotion =0;
    window.onscroll = (e) => {
        let scrollTop =window.scrollY;
        const scrollHeight = document.body.scrollHeight;
        if(scrollTop>oldPosiotion){
            oldPosiotion = scrollTop
            menuChooses.classList.add("active");
        }else{
            oldPosiotion = scrollTop
            menuChooses.classList.remove("active");
        }

    }
</script>
